    private void mueveSlider(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_mueveSlider
        Color color = new Color(sldRojo.getValue(), sldVerde.getValue(), sldAzul.getValue());
        lblColor.setBackground(color);
    }//GEN-LAST:event_mueveSlider

    private void btnEnviaColorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviaColorActionPerformed
        try {
            conexion = new Socket("localhost", 5555);
            System.out.println("El puerto local es: " + conexion.getLocalPort());

        } catch (IOException ex) {
            System.out.println("El servidor El Bombillo no responde");
        }
        linea = sldRojo.getValue() + "#" + sldVerde.getValue() + "#" + sldAzul.getValue() + "\n";
        System.out.println(linea);
        try {
            DataOutputStream outputStream = new DataOutputStream(conexion.getOutputStream());
            outputStream.writeBytes(linea);
            outputStream.flush();
        } catch (IOException ex) {
            System.out.println("Error al obtener un punto de entrada");
        }
        
        
    }//GEN-LAST:event_btnEnviaColorActionPerformed

